// Include theming for Angular Material with `mat.theme()`.
// This Sass mixin will define CSS variables that are used for styling Angular Material
// components according to the Material 3 design spec.
// Learn more about theming and how to use it for your application's
// custom components at https://material.angular.dev/guide/theming
@use '@angular/material' as mat;

// Custom Fonts
$cinzel-decorative: 'Cinzel Decorative', serif;
$libre-baskerville: 'Libre Baskerville', serif;
$merriweather: 'Merriweather', serif;
$im-fell-dw-pica: 'IM FELL DW Pica', serif;
$allura: 'Allura', cursive;
$edwardian-script-itc: 'Edwardian Script ITC', cursive;

html {
  @include mat.theme((
    color: (
      primary: mat.$azure-palette,
      tertiary: mat.$blue-palette,
    ),
    typography: Roboto,
    density: 0,
  ));

  // Default the application to a light color theme. This can be changed to
  // `dark` to enable the dark color theme, or to `light dark` to defer to the
  // user's system settings.
  color-scheme: dark;

  // Set a default background, font and text colors for the application using
  // Angular Material's system-level CSS variables. Learn more about these
  // variables at https://material.angular.dev/guide/system-variables
  background-color: var(--mat-sys-surface);
  color: var(--mat-sys-on-surface);
  font: var(--mat-sys-body-medium);
}/* You can add global styles to this file, and also import other style files */

// Apply to the HTML and Body elements
html, body { height: 100%; }
body {
  margin: 0;
  font-family: Roboto, "Helvetica Neue", sans-serif;
}

// Global Styling for Dark Mode
body.dark-mode {
  background-color: #121212;
  color: #E0E0E0;
}

// Light Mode (system-based)
body.light-mode {
  background-color: #F1F1F1;
  color: #333;
}

// Font customization for headers
h1, h2, h3 {
  font-family: $cinzel-decorative, serif;
}

// Content headers with Libre Baskerville
.content-header {
  font-family: $libre-baskerville, serif;
  font-weight: 700;
}

// Handwritten text with IM FELL DW Pica
.handwritten-text {
  font-family: $im-fell-dw-pica, serif;
  font-style: italic;
}

// Signatures with Allura
.signature {
  font-family: $allura, cursive;
  font-size: 1.5rem;
}

// Fine signatures with Edwardian Script ITC
.fine-signature {
  font-family: $edwardian-script-itc, cursive;
  font-size: 2rem;
}

// Flexbox layout container
.container {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
}

// Header styling
header {
  text-align: center;
  margin-bottom: 20px;
}

// Footer styling
footer {
  margin-top: 40px;
  text-align: center;
}

// Section styling for spacing
section {
  width: 100%;
  max-width: 900px;
  margin: 20px 0;
}

