// Imports
import { Application } from "https://deno.land/x/oak/mod.ts";

// Server
const app = new Application();

app.use( (ctx) => {
  ctx.response.body = "Welcome to the Sarim Port Authority WebApp!";
});

await app.listen( { port: 3000 } );

