// Imports
import { Router, RouterContext } from 'https://deno.land/x/oak/mod.ts';
import { DBHandler } from "../../Utilities/DBHandler.ts";
import { Notice } from "../../../frontend/src/app/TypeDefs.ts";

interface NoticeRes {
  status: number;
  message: string;
  success: boolean;
  notice?: Notice | null;
}

// Logic
const router = new Router();

router
  .get('/GetNotices', async ( ctx: RouterContext<string> ) => {
    const Mongo: DBHandler = ctx.state.Mongo;

    if ( parseInt( ctx.request.url.searchParams.get('noticeId') ) ) {
      const noticeId = parseInt( ctx.request.url.searchParams.get('noticeId') );
      const noticeRes = await Mongo.selectOneById( `SPA_Notices`, noticeId );

      if ( !noticeRes ) {
        console.log(`Notice with ID ${noticeId} not found.`);

        ctx.response.status = 404;
        ctx.response.body = {
          status: 404,
          message: 'Notice not found',
          success: false,
          notice: null
        } as NoticeRes;
        return;
      }

      ctx.response.body = {
        status: 200,
        message: 'Success',
        notice: noticeRes,
        success: true
      } as NoticeRes;
      return;
    }
    else {
      const limit = parseInt( ctx.request.url.searchParams.get('limit') || '6' );

      const noticesRes = await Mongo.selectMany( `SPA_Notices`, {}, { limit: limit } );

      return ctx.response.body = {
        status: 200,
        message: 'Success',
        notices: noticesRes,
        success: true
      };
    }
  });

export default {
  name: 'GetNotices',
  router: router
};

// Imports
import { Router, RouterContext } from 'https://deno.land/x/oak/mod.ts';
import { DBHandler } from "../../Utilities/DBHandler.ts";
import { Notice } from "../../../frontend/src/app/TypeDefs.ts";

interface NoticeRes {
  status: number;
  message: string;
  success: boolean;
  notice?: Notice | null;
}

// Logic
const router = new Router();

router
  .get('/GetNotices', async ( ctx: RouterContext<string> ) => {
    const Mongo: DBHandler = ctx.state.Mongo;

    if ( parseInt( ctx.request.url.searchParams.get('noticeId') ) ) {
      const noticeId = parseInt( ctx.request.url.searchParams.get('noticeId') );
      const noticeRes = await Mongo.selectOneById( `SPA_Notices`, noticeId );

      if ( !noticeRes ) {
        console.log(`Notice with ID ${noticeId} not found.`);

        ctx.response.status = 404;
        ctx.response.body = {
          status: 404,
          message: 'Notice not found',
          success: false,
          notice: null
        } as NoticeRes;
        return;
      }

      ctx.response.body = {
        status: 200,
        message: 'Success',
        notice: noticeRes,
        success: true
      } as NoticeRes;
      return;
    }
    else {
      const limit = parseInt( ctx.request.url.searchParams.get('limit') || '6' );
      const offset = parseInt( ctx.request.url.searchParams.get('offset') || '0' );

      const noticesRes = await Mongo.selectMany( `SPA_Notices`, {}, { limit: limit, skip: offset } );

      return ctx.response.body = {
        status: 200,
        message: 'Success',
        notices: noticesRes,
        success: true
      };
    }
  });

export default {
  name: 'GetNotices',
  router: router
};

import { Component } from '@angular/core';
import { DbHandlerService } from '../services/db-handler-service';
import { Notice } from '../TypeDefs';
import { NgTemplateOutlet } from '@angular/common';
import { NoticeBlurb } from '../notice-blurb/notice-blurb';

@Component({
  selector: 'app-notice-board',
  imports: [
    NgTemplateOutlet,
    NoticeBlurb
  ],
  templateUrl: './notice-board.html',
  styleUrl: './notice-board.scss'
})
export class NoticeBoard {
  notices: Notice[] = [];

  constructor( private dbService: DbHandlerService ) {
    this.loadNotices();
  }

  loadNotices() {
    this.dbService.getNotices().subscribe( ( noticesRes: any ) => {
      if ( noticesRes && noticesRes.length > 0 ) {
        console.log( `Found ${ noticesRes.length } notices.` );
      }
      else {
        console.log( 'No notices found.' );
      }
    } );
  }
}

import { Component } from '@angular/core';
import { DbHandlerService } from '../services/db-handler-service';
import { Notice } from '../TypeDefs';
import { NgTemplateOutlet } from '@angular/common';
import { NoticeBlurb } from '../notice-blurb/notice-blurb';

@Component({
  selector: 'app-notice-board',
  imports: [
    NgTemplateOutlet,
    NoticeBlurb
  ],
  templateUrl: './notice-board.html',
  styleUrl: './notice-board.scss'
})
export class NoticeBoard {
  notices: Notice[] = [];
  page = 1;
  pageSize = 16;
  loading = false;
  hasMore = true;

  constructor( private dbService: DbHandlerService ) {
    this.loadNotices();
  }

  loadNotices() {
    this.loading = true;
    const offset = ( this.page - 1 ) * this.pageSize;
    this.dbService.getNotices( this.pageSize, offset ).subscribe( ( notices: Notice[] ) => {
      this.notices = notices;
      this.loading = false;
      this.hasMore = notices.length === this.pageSize;
    } );
  }

  nextPage() {
    if ( this.hasMore ) {
      this.page++;
      this.loadNotices();
    }
  }

  prevPage() {
    if ( this.page > 1 ) {
      this.page--;
      this.loadNotices();
    }
  }
}

import { Component, Input } from '@angular/core';
import { NoticeBoard } from '../notice-board/notice-board';
import { DbHandlerService } from '../services/db-handler-service';
import { Notice } from '../TypeDefs';

@Component({
  selector: 'app-notices-page',
  imports: [
    NoticeBoard
  ],
  templateUrl: './notices-page.html',
  styleUrl: './notices-page.scss'
})
export class NoticesPage {
  @Input() notices: Notice[] = [];

  page = 1;
  pageSize = 16;
  loading = false;
  hasMore = true;

  constructor(private dbService: DbHandlerService) {
    this.loadNotices();
  }

  loadNotices() {
    this.loading = true;
    const offset = (this.page - 1) * this.pageSize;
    this.dbService.getNotices(this.pageSize, offset).subscribe((notices: Notice[]) => {
      this.notices = notices;
      this.loading = false;
      this.hasMore = notices.length === this.pageSize;
    });
  }

  nextPage() {
    if (this.hasMore) {
      this.page++;
      this.loadNotices();
    }
  }

  prevPage() {
    if (this.page > 1) {
      this.page--;
      this.loadNotices();
    }
  }
}

import { Component } from '@angular/core';
import { NoticeBoard } from '../notice-board/notice-board';

@Component({
  selector: 'app-notices-page',
  imports: [
    NoticeBoard
  ],
  templateUrl: './notices-page.html',
  styleUrl: './notices-page.scss'
})
export class NoticesPage {}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { catchError, map, Observable, of } from 'rxjs';
import { Department, NoticeRes } from '../TypeDefs';

@Injectable({
  providedIn: 'root'
})
export class DbHandlerService {

  private apiEndpoint = '/api/v1/';
  private noticesEndpoint = this.apiEndpoint + 'GetNotices';
  private recordsEndpoint = this.apiEndpoint + 'GetRecords';

  constructor ( private http: HttpClient ) {  }

  getNotices( limit?: number ) {
    return this.http.get(
      `${this.noticesEndpoint}?limit=${limit || 5}`,
    ).pipe(
      map( (data: any) => {
        if ( !data || !Array.isArray(data) ) {
          return [];
        }

        // Assuming the data is an array of notices
        return data.map((notice: any) => {
          return {
            id: notice.id,
            title: notice.title,
            content: notice.content,
            date: new Date(notice.date)
          };
        });
      } ),
      catchError( ( e: unknown ) => {
        console.error('Error fetching notices:', e);
        return [];
      } )
    );
  }

  getNoticeById(id: number): Observable<NoticeRes> {
    return this.http.get(`${this.noticesEndpoint}?id=${id}`).pipe(
      map((data: any) => {
        return {
          status: data.status,
          message: data.message,
          success: data.success,
          notice: data.notice || null
        } as NoticeRes;
      }),
      catchError(( e: any ) => {
        console.error('Error fetching notice by id:', e);

        if ( e.status === 500 ) {
          return of({
            status: 500,
            message: 'Error fetching notice',
            success: false,
            notice: null
          } as NoticeRes
          );
        }

        return of(
          {
            status: e.status || 500,
            message: e.message || 'Unknown error occurred',
            success: false,
            notice: null
          } as NoticeRes
        );
      })
    );
  }

  getDepartments() {
    return this.http.get(`${this.apiEndpoint}GetDepartments`).pipe(
      map((data: any) => {
        return data.departments;
      }),
      catchError((e: any) => {
        console.error('Error fetching departments:', e);
        return of([] as Department[]); // Return an empty array on error
      }
    ));
  }

  getRecords() {
    return this.http.get(this.recordsEndpoint);
  }
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { catchError, map, Observable, of } from 'rxjs';
import { Department, NoticeRes } from '../TypeDefs';

@Injectable({
  providedIn: 'root'
})
export class DbHandlerService {

  private apiEndpoint = '/api/v1/';
  private noticesEndpoint = this.apiEndpoint + 'GetNotices';
  private recordsEndpoint = this.apiEndpoint + 'GetRecords';

  constructor ( private http: HttpClient ) {  }

  getNotices( limit?: number, offset?: number ) {
    const params = [];
    if ( limit !== undefined ) params.push( `limit=${limit}` );
    if ( offset !== undefined ) params.push( `offset=${offset}` );
    const query = params.length ? `?${params.join('&')}` : '';
    return this.http.get(
      `${this.noticesEndpoint}${query}`,
    ).pipe(
      map( (data: any) => {
        // If API returns {notices: [...], ...}
        if ( data && Array.isArray(data.notices) ) {
          return data.notices;
        }
        // Fallback for old response
        if ( Array.isArray(data) ) {
          return data;
        }
        return [];
      } ),
      catchError( ( e: unknown ) => {
        console.error('Error fetching notices:', e);
        return [];
      } )
    );
  }

  getNoticeById(id: number): Observable<NoticeRes> {
    return this.http.get(`${this.noticesEndpoint}?id=${id}`).pipe(
      map((data: any) => {
        return {
          status: data.status,
          message: data.message,
          success: data.success,
          notice: data.notice || null
        } as NoticeRes;
      }),
      catchError(( e: any ) => {
        console.error('Error fetching notice by id:', e);

        if ( e.status === 500 ) {
          return of({
            status: 500,
            message: 'Error fetching notice',
            success: false,
            notice: null
          } as NoticeRes
          );
        }

        return of(
          {
            status: e.status || 500,
            message: e.message || 'Unknown error occurred',
            success: false,
            notice: null
          } as NoticeRes
        );
      })
    );
  }

  getDepartments() {
    return this.http.get(`${this.apiEndpoint}GetDepartments`).pipe(
      map((data: any) => {
        return data.departments;
      }),
      catchError((e: any) => {
        console.error('Error fetching departments:', e);
        return of([] as Department[]); // Return an empty array on error
      }
    ));
  }

  getRecords() {
    return this.http.get(this.recordsEndpoint);
  }
}

